mapper.select:
SELECT SI.SRTNCD, SI.ISINCD, SI.MRKTCTG, SI.ITMSNM, SI.CRNO, SI.CORPNM, SP.BASDT, SP.CLPR, SP.VS, SP.FLTRT, SP.MKP, SP.HIPR, SP.LOPR, SP.TRQU, SP.TRPRC, SP.LSTGSTCNT, SP.MRKTTOTAMT FROM STOCK_INFO SI INNER JOIN STOCK_PRICE SP ON SI.ISINCD = SP.ISINCD
AND SI.SRTNCD LIKE CONCAT('%', #{stockCode}, '%')
AND ( SI.ISINCD LIKE CONCAT('%', #{stockName}, '%') OR SI.ITMSNM LIKE CONCAT('%', #{stockName}, '%') )
AND SI.MRKTCTG = #{marketType}
AND SP.BASDT >= #{startDate}
AND SP.BASDT <= #{endDate}
AND SI.CORPNM = #{sector}

SELECT COUNT(*)
FROM
STOCK_INFO SI
INNER JOIN STOCK_PRICE SP
ON SI.ISINCD = SP.ISINCD



        AND SI.SRTNCD LIKE CONCAT('%', #{stockCode}, '%')



        AND (
        SI.ISINCD LIKE CONCAT('%', #{stockName}, '%')
        OR SI.ITMSNM LIKE CONCAT('%', #{stockName}, '%')
        )



        AND SI.MRKTCTG = #{marketType}



        AND SP.BASDT >= #{startDate}



        AND SP.BASDT <= #{endDate}



        AND SI.CORPNM = #{sector}

SELECT SI.SRTNCD, SI.ISINCD, SI.MRKTCTG, SI.ITMSNM, SI.CRNO, SI.CORPNM, DATE_FORMAT(SP.BASDT,'%Y-%m-%d') as BASDT, SP.CLPR, SP.VS, SP.FLTRT, SP.MKP, SP.HIPR, SP.LOPR, SP.TRQU, SP.TRPRC, SP.LSTGSTCNT, SP.MRKTTOTAMT FROM STOCK_INFO SI INNER JOIN STOCK_PRICE SP ON SI.ISINCD = SP.ISINCD
AND SI.SRTNCD LIKE CONCAT('%', #{stockCode}, '%')
AND ( SI.ISINCD LIKE CONCAT('%', #{stockName}, '%') OR SI.ITMSNM LIKE CONCAT('%', #{stockName}, '%') )
AND SI.MRKTCTG = #{marketType}
AND SP.BASDT >= #{startDate}
AND SP.BASDT <= #{endDate}
AND SI.CORPNM = #{sector}
ORDER BY SP.BASDT DESC, SI.ITMSNM LIMIT #{limit} OFFSET #{offset}

SELECT SI.CORPNM AS COMPANY_NAME, SI.SRTNCD AS STOCK_CODE, SI.MRKTCTG AS MARKET_CATEGORY, FIRST_DAY.BASDT AS START_DATE, FIRST_DAY.CLPR AS START_PRICE, LAST_DAY.BASDT AS END_DATE, LAST_DAY.CLPR AS END_PRICE, ROUND((LAST_DAY.CLPR - FIRST_DAY.CLPR) / FIRST_DAY.CLPR * 100, 2) AS PRICE_CHANGE_PCT FROM STOCK_INFO SI JOIN (SELECT SP.ISINCD, SP.BASDT, SP.CLPR FROM STOCK_PRICE SP WHERE SP.BASDT = (SELECT MIN(BASDT) FROM STOCK_PRICE WHERE ISINCD = SP.ISINCD AND BASDT BETWEEN STR_TO_DATE(#{startDate}, '%Y-%m-%d') AND STR_TO_DATE(#{endDate}, '%Y-%m-%d')) ) FIRST_DAY ON SI.ISINCD = FIRST_DAY.ISINCD JOIN (SELECT SP.SRTNCD, SP.BASDT, SP.CLPR, SP.ISINCD FROM STOCK_PRICE SP WHERE SP.BASDT = (SELECT MAX(BASDT) FROM STOCK_PRICE WHERE ISINCD = SP.ISINCD AND BASDT BETWEEN STR_TO_DATE(#{startDate}, '%Y-%m-%d') AND STR_TO_DATE(#{endDate}, '%Y-%m-%d')) ) LAST_DAY ON SI.ISINCD = LAST_DAY.ISINCD WHERE 1=1
AND SI.MRKTCTG = #{marketType}
ORDER BY PRICE_CHANGE_PCT DESC

SELECT COUNT(1) AS CNT
FROM STOCK_INFO
WHERE MRKTCTG = #{MRKTCTG}

mapper.delete:
DELETE FROM USER_FAVORITE_STOCKS
WHERE
FAVORITE_ID = #{srtnCd}
AND USER_ID = #{userId}
