mapper.delete:
DELETE FROM subway_bookmark
WHERE subwayId LIKE '%'  || #{subwayId}  || '%'
AND statnId LIKE '%'  || #{statnId}  || '%'
AND updnLine = #{updnLine}
AND userId = #{userId}

mapper.select:
SELECT COUNT(*) > 0
FROM subway_bookmark
WHERE subwayId LIKE '%'  || #{subwayId}  || '%'
    AND statnId LIKE '%'  || #{statnId}  || '%'
    AND updnLine = #{updnLine}
    AND userId = #{userId}
